import React from "react";
import logo from '../img/logo_veelo.png';
import '../css/Header.css';

const styles = {
    transition: 'all 0.5s ease-in-out'
};

class Header_Navbar extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            acceuilScale: 1,
            adminScale:0.7
        }

    }

    selectAcceuil(){
        this.setState({
            acceuilScale: 1,
            adminScale: 0.7
        })
    }

    selectAdmin(){
        this.setState({
            acceuilScale: 0.7,
            adminScale: 1
        })
    }

    render() {

        return (
            <div className="row pt-2 align-text-bottom">
                <div className="logo">
                    <a className="navbar-brand" href="#">
                        <img src={logo} style={{height: 50}}/>
                    </a>
                </div>
                <div className="acceuilSelect" style={{...styles,transform: 'scale(' + this.state.scale + ')'}}>
                    <a onClick={this.selectAcceuil.bind(this)}> <p>Acceuil</p></a>
                </div>
                <div className="adminNotSelect">
                    <a onClick={this.selectAdmin.bind(this)}><p>Administration</p></a>
                </div>
            </div>
        )
    }

}

export default Header_Navbar;
