import React from "react";
import logo from '../img/logo_veelo.png';
import '../css/Header.css';

//https://www.youtube.com/watch?v=8E6J0ZfeyBU
const styles = {
    transition: 'all 0.5s ease-in-out'
};

class Header_Navbar extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            acceuilScale: 1,
            acceuilPosition: 45,
            adminScale:0.7,
            adminPosition: 50
        }

    }

    selectAcceuil(){
        this.setState({
            acceuilScale: 1,
            acceuilPosition: 70,
            adminScale: 0.7,
            adminPosition: 0

        })
    }

    selectAdmin(){
        this.setState({
            acceuilScale: 0.7,
            acceuilPosition: 0,
            adminScale: 1,
            adminPosition: 70
        })
    }

    render() {

        return (
            <div className="row pt-2 align-text-bottom">
                <div className="logo">
                    <a className="navbar-brand" href="#">
                        <img src={logo} style={{height: 50}}/>
                    </a>
                </div>
                <div className="acceuilSelect" style={{...styles,transform: 'scale(' + this.state.acceuilScale + ') translate('+ this.state.adminPosition + 'px,0px)'}}>
                    <a onClick={this.selectAcceuil.bind(this)}><p>Acceuil</p></a>
                </div>
                <div className="adminNotSelect" style={{...styles,transform: 'scale(' + this.state.adminScale + ') translate(' + this.state.acceuilPosition + 'px,0px)'}}>
                    <a onClick={this.selectAdmin.bind(this)}><p>Administration</p></a>
                </div>
            </div>
        )
    }

}

export default Header_Navbar;
