import React from 'react';
import '../css/Header-Card.css';



class Header_Card extends React.Component {
    constructor(props){
        super(props);
        this.state({
            description: "Distance parcourue /jour",
            value: 45.2,
            growth: 3.5,
            color: "green"
        });
    }



    render() {
        if(this.state.growth<0){
            this.setState({color:"red"});
        }
        return (
            <div className="col-lg-4 my-5">
                <div className="fontHeaderCard">
                    <div className="card mx-3 p-3">
                        <h2 className="h2HeaderCard">{this.state.description}</h2>
                        <h1>{this.state.value}</h1>
                        <h4 style={{color: this.state.color}}>{this.state.growth}%</h4>
                    </div>
                </div>
            </div>
        )
    }

}

export default Header_Card;
