import React from 'react';
import {
    BrowserRouter as Router,
    Switch,
} from "react-router-dom";

import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col";
import Container from 'react-bootstrap/Container';

//Components
import Header_Card from "./components/Header-Card";
import Header_Navbar from "./components/Header-Navbar";
import Main_Card_Graph from "./components/Main-Card-Graph";
import Main_Card_Info from "./components/Main-Card-Info";
import Main_Card_Classemnt from "./components/Main-Card-Classement";
import Main_Card_Map from "./components/Main-Card-MapRadar";

//CSS
import './App.css';

const styles = {
    transition: 'all 0.3s ease-out'
};

const AnimatedSwitch = ({ animationClassName, animationTimeout, children }) => (
    <Route render={({ location }) => (
        <TransitionGroup style={{
            flex: 1,
            position: 'relative',
        }}>
            <CSSTransition
                key={location.key}
                timeout={animationTimeout}
                classNames={animationClassName}
            >
                <Switch location={location}>
                    {children}
                </Switch>
            </CSSTransition>
        </TransitionGroup>
    )} />
);

const AnimatedRoute = (props) => (
    <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
    }}>
        <Route {...props} />
    </div>
);

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            acceuilPosition :0,
            fade : 0
        }
    }

    selectAdmin(){
        this.setState({
            acceuilPosition : -500,
            fade : 1
        })
    }




    render() {
        return (
            <Router>
                <div className="App">
                    <header className="header-body bg-gradient-info">
                        <div className="container-fluid">
                            <Header_Navbar/>
                            <div className="row mx-5 justify-content-center">
                                <Header_Card/>
                                <Header_Card/>
                                <Header_Card/>
                            </div>
                        </div>
                    </header>
                    <AnimatedSwitch
                        animationClassName="page-slide"
                        animationTimeout={300}
                    >
                        <AnimatedRoute exact path="/"><Acceuil/></AnimatedRoute>
                        <AnimatedRoute path="administration" render={() => (
                            <div>Je te baise</div>
                        )}/>
                    </AnimatedSwitch>
                </div>
            </Router>
        );
    }
}

const Administration = () => (
    <div>
        <div>Je te baise</div>
    </div>
);

const Acceuil = () => (
    <div>
        <Container fluid className="main">
            <Row className="mx-5 justify-content-center middle-row">
                <Col className="col-lg-8 col-md-10 col-12 mt-lg-5 mt-2 justify-content-around">
                    <Main_Card_Graph/>
                </Col>
                <Col className="col-lg-4 col-md-10 col-12 mt-lg-5 mt-2 justify-content-around">
                    <Main_Card_Info/>
                </Col>
            </Row>
            <Row className="mx-5 justify-content-center">
                <Col className="col-lg-6 col-md-10 col-12 mt-lg-5 mt-2 justify-content-around">
                    <Main_Card_Classemnt/>
                </Col>
                <Col className="col-lg-6 col-md-10 col-12 mt-lg-5 mt-2 justify-content-around">
                    <Main_Card_Map/>
                </Col>
            </Row>
        </Container>
    </div>
);

export default App;
